css:
article > header {
  background: url("http://res.cloudinary.com/dagilleland/image/upload/v1507752947/UnSplash/riku-lu-4171.jpg") no-repeat scroll center top #000;
  background-size:cover;
  min-height: 5em;
  padding: 15px;
}
header > h1 {
  font-size: 2.25em;
  padding: 0;
  margin: 0;
}

@import url('https://fonts.googleapis.com/css?family=Roboto');

/* Global Settings */
* {
  box-sizing: border-box;
}

/* Page Layout */
body {
  margin: 0;
  padding: 0;
  display: grid;
  grid-template-columns: min-content;
  grid-template-rows: min-content;
}

body > * {
  border: solid 1px gray;
}

body > header {
  grid-column: 1;
  grid-row: 1;
}

body > nav {
  grid-column: 2 / span 4;
  grid-row: 1;
}

body > main {
  grid-column: 1 / span 5;
  grid-row: 2;
}

/* Fonts and Colors */

body {
  font-family: "Roboto", Helvetica, Arial, sans-serif;
  font-size: 18px;
}

body > header, body > nav, body > nav a {
  background-color: black;
  color: #999999;
}

body > header h1 {
  font-size: 1.5em;
  margin: 0;
  padding: 10px 15px;
  white-space: nowrap;
}

/* Navigation Structure/styles */
body > nav ul {
  list-style: none;
  display: flex;
  padding: 0;
  margin: 0;
}

body > nav li {
  font-size: 1.2em;
  font-weight: bold;
  padding: 10px 15px;
}

body > nav a {
  text-decoration: none;
}

/* Main Content Styling */
main {
  padding: 10px;
  columns: 3;
}

main > h1:first-child {
  column-span: all;
}

main > h1 {
  font-size: 1.5em;
}

main > h1:first-child + p:first-letter {
  float: left;
  font-weight: bold;
  font-style: normal;
  font-size: 2em;
  line-height: 1;
  margin-right: 9px;
  background-color: aqua;
  border-radius: 5px;
  padding: 5px;
  margin: 0;
  margin-right: 2px;
}

main > p:first-of-type {
  font-size: 1.1em;
  font-style:italic;
}

/* Class-specific styles */
.float {
  float: right;
  display: inline-block;

}
